mapper.select:
SELECT oid, mid, odate, ostatus, amount, postcode, address, extraAddress, detailAddress, oname, ophone
FROM zava_order
WHERE mid = #{mid}

mapper.insert:
INSERT INTO zava_order_detail(oDid, oid, pid, pcnt)
	SELECT zava_order_detail_seq.nextval, #{oid}, #{pid}, #{pcnt}
		FROM zava_cart
